@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-success: var(--success);
  --color-success-foreground: var(--success-foreground);
  --color-warning: var(--warning);
  --color-warning-foreground: var(--warning-foreground);
  --font-sans: var(--font-family);
}

:root {
  --radius: 0.75rem;
  --font-family: 'Plus Jakarta Sans', sans-serif;

  /* App background - cool off-white */
  --background: hsl(220, 20%, 97%);
  --foreground: hsl(222, 47%, 11%);

  /* Cards */
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 47%, 11%);

  /* Popover */
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 47%, 11%);

  /* Primary - warm amber */
  --primary: hsl(38, 92%, 50%);
  --primary-foreground: hsl(222, 47%, 11%);

  /* Secondary */
  --secondary: hsl(220, 13%, 91%);
  --secondary-foreground: hsl(222, 47%, 11%);

  /* Muted */
  --muted: hsl(220, 13%, 94%);
  --muted-foreground: hsl(220, 9%, 46%);

  /* Accent */
  --accent: hsl(38, 92%, 96%);
  --accent-foreground: hsl(38, 92%, 30%);

  /* Destructive */
  --destructive: hsl(0, 72%, 51%);

  /* Border/Input/Ring */
  --border: hsl(220, 13%, 91%);
  --input: hsl(220, 13%, 91%);
  --ring: hsl(38, 92%, 50%);

  /* Success */
  --success: hsl(142, 71%, 45%);
  --success-foreground: hsl(0, 0%, 100%);

  /* Warning */
  --warning: hsl(38, 92%, 50%);
  --warning-foreground: hsl(222, 47%, 11%);

  /* Sidebar - deep navy */
  --sidebar: hsl(222, 47%, 11%);
  --sidebar-foreground: hsl(215, 20%, 65%);
  --sidebar-primary: hsl(38, 92%, 50%);
  --sidebar-primary-foreground: hsl(222, 47%, 11%);
  --sidebar-accent: hsl(222, 35%, 18%);
  --sidebar-accent-foreground: hsl(215, 30%, 85%);
  --sidebar-border: hsl(222, 35%, 18%);
  --sidebar-ring: hsl(38, 92%, 50%);

  /* Custom design tokens */
  --gradient-primary: linear-gradient(135deg, hsl(38, 92%, 50%), hsl(38, 95%, 60%));
  --gradient-hero: linear-gradient(135deg, hsl(222, 47%, 13%), hsl(222, 40%, 20%));
  --gradient-card: linear-gradient(145deg, hsl(0, 0%, 100%), hsl(220, 20%, 98%));
  --shadow-card: 0 1px 3px hsl(220, 13%, 60% / 0.12), 0 1px 2px hsl(220, 13%, 60% / 0.08);
  --shadow-elevated: 0 4px 12px hsl(220, 13%, 60% / 0.15), 0 2px 4px hsl(220, 13%, 60% / 0.08);
  --shadow-modal: 0 20px 60px hsl(222, 47%, 11% / 0.18), 0 8px 24px hsl(222, 47%, 11% / 0.1);
  --shadow-amber: 0 4px 14px hsl(38, 92%, 50% / 0.35);
  --transition-smooth: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  --transition-bounce: all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-family);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
}

/* Sidebar styles */
.sidebar-nav-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 9px 12px;
  border-radius: 8px;
  font-size: 0.875rem;
  font-weight: 500;
  color: hsl(215, 20%, 65%);
  transition: var(--transition-smooth);
  cursor: pointer;
  text-decoration: none;
  position: relative;
}

.sidebar-nav-item:hover {
  background: hsl(222, 35%, 18%);
  color: hsl(215, 30%, 85%);
}

.sidebar-nav-item.active {
  background: hsl(222, 35%, 18%);
  color: hsl(38, 92%, 50%);
  font-weight: 600;
}

.sidebar-nav-item.active::before {
  content: '';
  position: absolute;
  left: 0;
  top: 6px;
  bottom: 6px;
  width: 3px;
  background: hsl(38, 92%, 50%);
  border-radius: 0 3px 3px 0;
}

/* KPI card styles */
.kpi-card {
  background: var(--gradient-card);
  border: 1px solid hsl(220, 13%, 91%);
  border-radius: 12px;
  box-shadow: var(--shadow-card);
  padding: 24px;
  transition: var(--transition-smooth);
}

.kpi-card:hover {
  box-shadow: var(--shadow-elevated);
  transform: translateY(-1px);
}

.kpi-hero {
  background: var(--gradient-hero);
  border: 1px solid hsl(222, 35%, 20%);
  border-radius: 12px;
  box-shadow: var(--shadow-elevated);
  padding: 28px;
  color: hsl(0, 0%, 100%);
  transition: var(--transition-smooth);
}

.kpi-hero:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px hsl(222, 47%, 11% / 0.25);
}

/* Table styles */
.data-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.875rem;
}

.data-table th {
  background: hsl(220, 20%, 97%);
  padding: 10px 16px;
  text-align: left;
  font-weight: 600;
  font-size: 0.75rem;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  color: hsl(220, 9%, 46%);
  border-bottom: 1px solid hsl(220, 13%, 91%);
}

.data-table td {
  padding: 12px 16px;
  border-bottom: 1px solid hsl(220, 13%, 94%);
  color: hsl(222, 47%, 11%);
}

.data-table tr:last-child td {
  border-bottom: none;
}

.data-table tbody tr:hover td {
  background: hsl(220, 20%, 98%);
}

/* Badge variants */
.badge-paid {
  display: inline-flex;
  align-items: center;
  gap: 4px;
  padding: 2px 10px;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 600;
  background: hsl(142, 71%, 94%);
  color: hsl(142, 55%, 28%);
  border: 1px solid hsl(142, 71%, 82%);
}

.badge-unpaid {
  display: inline-flex;
  align-items: center;
  gap: 4px;
  padding: 2px 10px;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 600;
  background: hsl(0, 72%, 96%);
  color: hsl(0, 65%, 45%);
  border: 1px solid hsl(0, 72%, 88%);
  animation: pulse-unpaid 2s ease-in-out infinite;
}

@keyframes pulse-unpaid {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.75; }
}

/* Form dialog */
.form-section-label {
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: hsl(220, 9%, 46%);
  margin-bottom: 12px;
  margin-top: 20px;
}

/* Scrollbar */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}
::-webkit-scrollbar-track {
  background: transparent;
}
::-webkit-scrollbar-thumb {
  background: hsl(220, 13%, 85%);
  border-radius: 3px;
}
::-webkit-scrollbar-thumb:hover {
  background: hsl(220, 9%, 70%);
}

/* Page transition */
.page-enter {
  animation: fadeSlideIn 0.2s ease-out;
}

@keyframes fadeSlideIn {
  from {
    opacity: 0;
    transform: translateY(6px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Empty state */
.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 64px 24px;
  color: hsl(220, 9%, 60%);
  text-align: center;
}

.empty-state-icon {
  width: 48px;
  height: 48px;
  background: hsl(220, 13%, 94%);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 16px;
  color: hsl(220, 9%, 60%);
}
